import React from 'react';

const PersonalInfo = () => {
  return (
    <section className="mb-12">
      <h1 className="text-4xl font-bold mb-2">Naveen Kumar J</h1>
      <h2 className="text-xl text-indigo-600 dark:text-indigo-400 mb-4">
        Aspiring DevOps Engineer | Cloud Enthusiast
      </h2>
      <p className="max-w-3xl text-gray-700 dark:text-gray-300">
        I am a passionate DevOps engineer and cloud enthusiast with a background in software development. I enjoy automating workflows, optimizing cloud infrastructure, and continuously learning new technologies to build scalable and reliable systems. My goal is to contribute to innovative projects and grow as a cloud-native expert.
      </p>
    </section>
  );
};

export default PersonalInfo;
